import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {
 footerBrand:string= "assets/images/footer-logo.png";
 socialLink:string= "assets/images/social-footer.png";
 googleplay:String = "assets/images/googleplay.png";
 download:String= "assets/images/download.png";
 downloadImg:string = "assets/images/download.png";

}
<div class="container">
    <div class="row">
      <div class="col-lg-6">
        <h3>Download Our App Now!</h3>
        <p>The rise of mobile device transforms the way we consume information entirely and the world's most elevant channels such as Facebook</p>

      </div>
      <div class="col-lg-6 download"  [style.background]="downloadImg">

      </div>
      <div class="row"> <img [src]="googleplay" alt="">
        <img [src]="download" alt=""></div>

    </div>
</div>

<footer class="text-center">
    <img [src]="footerBrand" width="100">
  <ul class="" style="list-style: none; font-weight: 200; font-size: 18px; color:rgb(251, 251, 251);">
    <li><a>Home</a></li><li><a>Key Features</a></li><li><a>Pricing</a></li><li><a>Testimonial</a></li><li><a>FAQ</a></li></ul>  
  <img [src]="socialLink" width="110">
  <p style="color:#5f636e; font-size: 18px;">Copyright@ Arifur Rahman Tushar 2019 All rights reserved</p>
</footer>
